mapper.select:
 select * from board
left join customer on customer.code=board.customer_code
where no=#{no}

mapper.update:
update board 
set title=#{title}, content=#{content}, airport_code=#{airportCode.code}, open=#{open}, required=#{required}
where no = #{no}

update board 
set view_cnt=view_cnt+1
where no = #{no}
